import React, { useContext, useEffect, useState } from "react";
import { AuthContext } from "../context/AuthContext";
import { useNavigate } from "react-router-dom";
import { listApplications, replyToApplication } from '../api/api';
import jsPDF from "jspdf";

export default function AdminDashboard() {
  const { token, logout } = useContext(AuthContext);
  const navigate = useNavigate();
  const [applications, setApplications] = useState([]);
  const [replyText, setReplyText] = useState({});
  const [statusText, setStatusText] = useState({});

  // Fetch applications
  useEffect(() => {
    if (!token) return navigate("/admin/login");

    const fetchApplications = async () => {
      try {
        const res = await api.get("/applications", {
          headers: { Authorization: `Bearer ${token}` },
        });
        setApplications(res.data);
      } catch (err) {
        alert("Failed to load applications");
      }
    };

    fetchApplications();
  }, [token, navigate]);

  // Reply to application
  const handleReply = async (appId) => {
    try {
      const res = await api.post(
        `/applications/reply/${appId}`,
        {
          reply: replyText[appId] || "",
          status: statusText[appId] || "Replied",
        },
        { headers: { Authorization: `Bearer ${token}` } }
      );

      setApplications((prev) =>
        prev.map((app) => (app._id === appId ? res.data : app))
      );

      setReplyText((prev) => ({ ...prev, [appId]: "" }));
      setStatusText((prev) => ({ ...prev, [appId]: "" }));
    } catch (err) {
      alert("Failed to send reply");
    }
  };

  const fileUrl = (filename) =>
    filename ? `http://localhost:5000/uploads/${filename}` : null;

  // Generate styled PDF with clickable links
  const downloadPDF = () => {
    const doc = new jsPDF();
    doc.setFontSize(18);
    doc.text("Applications List", 105, 15, { align: "center" });
    let y = 30;

    applications.forEach((app, index) => {
      // Box around each application
      doc.setDrawColor(0);
      doc.setLineWidth(0.5);
      doc.rect(8, y - 8, 194, 50 + 7 * 2); // width, height (estimate)

      doc.setFontSize(12);
      doc.setFont("helvetica", "bold");
      doc.text(`Application #${index + 1}`, 10, y);
      y += 7;

      doc.setFont("helvetica", "normal");
      doc.text(`Full Name: ${app.fullname}`, 10, y);
      y += 6;
      doc.text(`Email: ${app.email}`, 10, y);
      y += 6;
      doc.text(`Job Type: ${app.jobType}`, 10, y);
      y += 6;
      doc.text(`Job Position: ${app.jobPosition}`, 10, y);
      y += 6;
      doc.text(`Status: ${app.status || "Pending"}`, 10, y);
      y += 6;
      doc.text(`Reply: ${app.reply || "-"}`, 10, y);
      y += 6;

      // Clickable file links
      if (app.proofFile) {
        const proofURL = fileUrl(app.proofFile);
        doc.setTextColor(0, 0, 255);
        doc.textWithLink("View Proof", 10, y, { url: proofURL });
        y += 6;
      }
      if (app.resumeFile) {
        const resumeURL = fileUrl(app.resumeFile);
        doc.setTextColor(0, 0, 255);
        doc.textWithLink("View Resume", 10, y, { url: resumeURL });
        y += 6;
      }

      doc.setTextColor(0, 0, 0); // Reset color
      y += 10;

      // Add new page if y exceeds page height
      if (y > 270) {
        doc.addPage();
        y = 20;
      }
    });

    doc.save("applications.pdf");
  };

  return (
    <div className="max-w-4xl mx-auto mt-6">
      <h1 className="text-2xl font-bold mb-4">Admin Dashboard</h1>

      <div className="flex gap-4 mb-6">
        <button
          onClick={() => {
            logout();
            navigate("/admin/login");
          }}
          className="bg-red-500 text-white p-2 rounded"
        >
          Logout
        </button>

        <button
          onClick={downloadPDF}
          className="bg-green-500 text-white p-2 rounded"
        >
          Download PDF
        </button>
      </div>

      <div className="space-y-6">
        {applications.map((app) => (
          <div
            key={app._id}
            className="p-4 border rounded bg-white shadow-sm"
          >
            <p><strong>Full Name:</strong> {app.fullname}</p>
            <p><strong>Email:</strong> {app.email}</p>
            <p><strong>Job Type:</strong> {app.jobType}</p>
            <p><strong>Job Position:</strong> {app.jobPosition}</p>
            <p><strong>Status:</strong> {app.status || "Pending"}</p>
            <p><strong>Reply:</strong> {app.reply || "-"}</p>

            {/* File Links */}
            <div className="mt-2">
              {app.proofFile && (
                <a
                  href={fileUrl(app.proofFile)}
                  target="_blank"
                  rel="noopener noreferrer"
                  className="text-blue-500 underline mr-4"
                >
                  View Proof
                </a>
              )}
              {app.resumeFile && (
                <a
                  href={fileUrl(app.resumeFile)}
                  target="_blank"
                  rel="noopener noreferrer"
                  className="text-blue-500 underline"
                >
                  View Resume
                </a>
              )}
            </div>

            {/* Reply Form */}
            <div className="mt-2 flex flex-col gap-2">
              <textarea
                placeholder="Type your reply here..."
                value={replyText[app._id] || ""}
                onChange={(e) =>
                  setReplyText({ ...replyText, [app._id]: e.target.value })
                }
                className="border p-2 rounded w-full"
              />
              <input
                type="text"
                placeholder="Optional status (e.g., Approved)"
                value={statusText[app._id] || ""}
                onChange={(e) =>
                  setStatusText({ ...statusText, [app._id]: e.target.value })
                }
                className="border p-2 rounded w-full"
              />
              <button
                onClick={() => handleReply(app._id)}
                className="bg-blue-500 text-white p-2 rounded"
              >
                Send Reply
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
