import React, { useState, useEffect } from 'react';
import { uploadFiles } from '../api/api';
import { useLocation, useNavigate } from 'react-router-dom';

function useQuery() {
  return new URLSearchParams(useLocation().search);
}

export default function ProofUpload() {
  const query = useQuery();
  const id = query.get('id');
  const navigate = useNavigate();

  const [fullname, setFullname] = useState('');
  const [proof, setProof] = useState(null);
  const [cv, setCv] = useState(null);

  useEffect(() => {
    // optional: if id absent, redirect to form
    if (!id) {
      // navigate('/');
    }
  }, [id, navigate]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!id) return alert('Missing application id. Please start from the form.');
    try {
      const fd = new FormData();
      fd.append('proof', proof);
      fd.append('cv', cv);
      const res = await uploadFiles(id, fd);
      alert('Files uploaded successfully');
      navigate('/reply');
    } catch (err) {
      console.error(err);
      alert('Upload failed');
    }
  };

  return (
    <div className="max-w-md mx-auto p-4 bg-white rounded shadow">
      <h2 className="text-xl font-bold mb-3">Proof of Payment & CV Upload</h2>
      <form onSubmit={handleSubmit} className="space-y-3">
        <input value={fullname} onChange={(e)=>setFullname(e.target.value)} placeholder="Full name" className="w-full p-2 border rounded" />

        <div>
          <label className="block mb-1">Proof of Payment</label>
          <input type="file" accept=".jpg,.jpeg,.png,.pdf" onChange={(e)=>setProof(e.target.files[0])} required />
        </div>

        <div>
          <label className="block mb-1">CV / Resume</label>
          <input type="file" accept=".pdf,.doc,.docx" onChange={(e)=>setCv(e.target.files[0])} required />
        </div>

        <button type="submit" className="w-full p-2 bg-green-600 text-white rounded">Submit Application</button>
      </form>
    </div>
  );
}
