import React, { useEffect, useState } from 'react';
import { listApplications, replyToApplication } from '../api/api';

export default function AdminDashboard() {
  const [apps, setApps] = useState([]);
  const [replies, setReplies] = useState({});
  const token = localStorage.getItem('adminToken');

  useEffect(() => {
    if (!token) return; // redirect handled elsewhere
    fetchApps();
  }, [token]);

  const fetchApps = async () => {
    try {
      const res = await listApplications(token);
      setApps(res.data);
    } catch (err) {
      console.error(err);
      alert('Failed to fetch applications. Make sure you are logged in as admin.');
    }
  };

  const handleChange = (id, value) => setReplies(prev => ({ ...prev, [id]: value }));

  const handleSend = async (id) => {
    try {
      const payload = { reply: replies[id] || '' };
      await replyToApplication(id, payload, token);
      alert('Reply saved');
      fetchApps();
    } catch (err) {
      console.error(err);
      alert('Failed to save reply');
    }
  };

  return (
    <div className="p-6 min-h-screen bg-gray-100">
      <div className="max-w-5xl mx-auto">
        <h1 className="text-2xl font-bold mb-4">Admin Dashboard — Applications</h1>
        {apps.length === 0 && <div className="p-4 bg-white rounded shadow">No applications yet.</div>}

        <div className="space-y-4">
          {apps.map(app => (
            <div key={app._id} className="bg-white p-4 rounded shadow">
              <div className="flex justify-between">
                <div>
                  <div className="font-semibold">{app.fullname} — {app.jobPosition} ({app.jobType})</div>
                  <div className="text-sm text-gray-600">{app.email} | {app.mobile}</div>
                  <div className="text-xs text-gray-500">Submitted: {new Date(app.createdAt).toLocaleString()}</div>
                  {app.status && <div className="mt-1 text-sm">Status: <span className="font-medium">{app.status}</span></div>}
                </div>
                <div className="text-right">
                  {app.resumeFile && <a href={`${process.env.REACT_APP_API_BASE || ''}/uploads/${app.resumeFile}`} target="_blank" rel="noreferrer" className="block underline">View CV</a>}
                  {app.proofFile && <a href={`${process.env.REACT_APP_API_BASE || ''}/uploads/${app.proofFile}`} target="_blank" rel="noreferrer" className="block underline">View Proof</a>}
                </div>
              </div>

              <textarea
                value={replies[app._id] ?? app.reply ?? ''}
                onChange={(e) => handleChange(app._id, e.target.value)}
                placeholder="Write reply to applicant..."
                className="w-full mt-3 p-2 border rounded h-24"
              />

              <div className="flex gap-2 mt-2">
                <button onClick={() => handleSend(app._id)} className="px-4 py-2 bg-blue-600 text-white rounded">Save Reply</button>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}
