// pages/ProofUpload.jsx
import React, { useState } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import { uploadFiles } from '../api/api';

function useQuery() {
  return new URLSearchParams(useLocation().search);
}

export default function ProofUpload() {
  const query = useQuery();
  const id = query.get('id');
  const navigate = useNavigate();

  const [proofFile, setProofFile] = useState(null);
  const [resumeFile, setResumeFile] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!id) return alert('Missing application id. Start from form.');

    try {
      const formData = new FormData();
      formData.append('proofFile', proofFile);
      formData.append('resumeFile', resumeFile);

      await uploadFiles(id, formData);
      alert('Files uploaded successfully');
      navigate('/reply'); // or wherever next
    } catch (err) {
      console.error(err);
      alert('Upload failed');
    }
  };

  return (
    <div className="max-w-md mx-auto p-4 bg-white rounded shadow">
      <h2 className="text-xl font-bold mb-3">Proof of Payment & CV Upload</h2>
      <form onSubmit={handleSubmit} className="space-y-3">
        <div>
          <label>Proof of Payment</label>
          <input type="file" accept=".jpg,.jpeg,.png,.pdf" onChange={(e) => setProofFile(e.target.files[0])} required />
        </div>

        <div>
          <label>CV / Resume</label>
          <input type="file" accept=".pdf,.doc,.docx" onChange={(e) => setResumeFile(e.target.files[0])} required />
        </div>

        <button type="submit" className="w-full p-2 bg-green-600 text-white rounded">Submit Application</button>
      </form>
    </div>
  );
}
